import { STORE_USER_REPOS,SELECTED_REPO } from '../actions'

const github = (
    state = {},
    action
) => {
    switch (action.type) {
        case STORE_USER_REPOS:
            return Object.assign({}, state, {
                ...action.payload
            })
        case SELECTED_REPO:
            return Object.assign({}, state, {
               selected : {
                   ...action.payload
               }
            })
        default:
            return state
    }
}

export default github
