import { call, put, take } from 'redux-saga/effects'
import  { getUserRepos } from '../../../apis/github.api'
import { githubActions } from '../../actions'
import { setUser } from '../../../infra/local-storage'

function* fetchUserRepos(action) {
  try {
    yield call(getUserRepos,action.payload)
    yield put(githubActions.store(action.payload))
  } catch (ex) {
    yield put(githubActions.fail(ex))
  }
}

export default fetchUserRepos;
