import React from 'react'
import {connect} from "react-redux";
import {GET_USER_REPOS, getReposList, getRepoSelected} from "../../redux/actions";

class ListCommits extends React.Component {
    constructor(props){
        super(props);
        this.state = {
            filter : ''
        }
    }
    componentDidMount() {

    }

    onFilterCommits = (e) =>{
        this.setState({ filter : e.target.value})
    }
    render(){
        const { commits,repo } = this.props
        return (
            <React.Fragment>
                Reposit√≥rio: {repo}
                Digite o nome do commmit <input type='text' onChange={this.onFilterCommits} value={this.state.filter} />
                <table>
                    <thead>
                    <tr>
                        <th>Commits</th>
                    </tr>
                    </thead>
                    <tbody>
                    {commits && commits.filter((item)=>(
                       item.name === this.state.filter && <tr>
                            <td>{item.name}</td>
                        </tr>
                    ))}
                    </tbody>
                </table>
            </React.Fragment>
        )
    }
}

const mapStateToProps = state => {
    return {
        commits: getReposList(state),
        repo: getRepoSelected(state)
    }
}
const mapDispatchToProps = dispatch => ({
    onGetRepos (user) {
        dispatch({
            type: GET_USER_REPOS,
            payload: {user}
        })
    }
})

export default connect((state) =>
        mapStateToProps,
        mapDispatchToProps
)(ListCommits)
