export const GET_USER_REPOS = '[USER REPOS] POST'
export const STORE_USER_REPOS = '[USER REPOS] STORE'
export const SUCCESS_USER_REPOS = '[USER SUCESS] SUCESS'
export const FAIL_USER_REPOS = '[USER REPOS] FAIL'

export const githubActions = {
    getRepos: ({user}) => ({
        type : GET_USER_REPOS,
        payload: {user}
    }),
    store: data => ({
      type: STORE_USER_REPOS,
      payload: data
    }),
    fail: error => ({
      type: FAIL_USER_REPOS,
      payload: error
    }),
  }

export function getUser(state){
    const { user } = state;
    console.log('user',user)
    return user;
 }


export function getRepos(state){
    const { repos } = state;
    console.log('repos',repos)
    return repos;
}
