import React,{Component} from 'react'
import {startCommitsSaga} from '../../redux/sagas'

class SearchUser extends Component{
    constructor(props) {
        super(props)
        this.state = { user : ''}
    }
    onGetRepos = () =>{
        this.props.onGetRepos(this.state.user)
    }
    setUser = (e) => {
        this.setState({user : e.target.value})
    }
    render() {
        const { repos } = this.props
        return (
            <React.Fragment>
                Digite o usuário <input type='text' value={this.state.user} onChange={this.setUser}/>
                <button onClick={this.onGetRepos}>Pesquisar</button>
                <p>Total {repos && repos.length} </p>
                <table>
                    <thead>
                    <tr>
                        <th>Repositórios</th>
                        <th></th>
                    </tr>
                    </thead>
                    <tbody>
                    {repos && repos.map((item)=>(
                        <tr>
                            <td>{item.name}</td>
                            <td><a href={`/${this.state.user}/${item.name}/commits`}>Ver commits</a></td>
                        </tr>
                    ))}
                    </tbody>
                </table>
            </React.Fragment>

        )
    }
}

export default SearchUser
