import { START_LOADING,STOP_LOADING } from '../../actions/loading'

const loadingReducer = (
    state = false,
    action
) => {
    switch (action.type) {
        case START_LOADING:
            return Object.assign({}, state,true)
        case STOP_LOADING:
            return Object.assign({}, state,false)
        default:
            return state
    }
}

export default loadingReducer
