import { takeEvery, all } from 'redux-saga/effects'
import { GET_USER_REPOS,SELECTED_REPO } from '../actions'
import fetchUserRepos from './repos'
import fetchCommits from './commits'


function* rootSaga() {
  yield all([
          takeEvery(GET_USER_REPOS, fetchUserRepos),
          takeEvery(SELECTED_REPO,fetchCommits)
        ])
}



export default rootSaga;
