import { call, put,takeEvery } from 'redux-saga/effects'
import { getRepoCommits} from '../../../apis/github.api'
import {GET_USER_REPOS, githubActions} from '../../actions'

function* fetchCommits(action){
    try{
        const { data }  = yield call(getRepoCommits,action.payload)
        yield put(githubActions.store({ commits : data}))
    }catch (e) {
        yield put(githubActions.fail(e))
    }
}

export function* commitsSaga() {
    yield takeEvery(GET_USER_REPOS, fetchCommits)
}
