import React from 'react';
import { connect } from 'react-redux'
import { githubActions, getUser } from './redux/actions/github-actions'
import { startServer  } from './infra/api-config'
import { startSagaRoot } from './redux'
import SearchUser from './pages/seachUser'
import {applyMiddleware, createStore} from "redux";
import reducer from "./redux/reducers";


const store = createStore(
    reducer,
    composeEnhancers(
        applyMiddleware(sagaMiddleware)
    )
)

class App extends React.Component{
    constructor(props){
        super(props)
    }
    componentDidMount() {
        startServer()
        startSagaRoot()
    }
    onGetRepos = () =>{
        this.props.onGetRepos(this.state.user)
    }
    render(){
        const { user } = this.props
        return (
            <React.Fragment>
                <SearchUser onGetRepos={this.onGetRepos}  user={this.user} />
            </React.Fragment>
       );
    }
}

export default App
