import { takeEvery } from 'redux-saga/effects'
import { GET_USER_REPOS,SELECTED_REPO } from '../actions'
import fetchUserRepos from './repos'
import fetchCommits from './commits'

export function* reposSaga() {
    yield takeEvery(GET_USER_REPOS, fetchUserRepos)
}
export function* commitsSaga() {
    yield takeEvery(SELECTED_REPO,fetchCommits)
}
