import { call, put, take } from 'redux-saga/effects'
import  { getUserRepos } from '../../../apis/github.api'
import { STORE_USER, githubActions, userActions } from '../../actions'
import { setUser } from '../../../infra/local-storage'

function* fetchUserRepos(action) {
  try {
    yield call(getRepos,action.payload)
    yield put(userActions.store(action.payload))
    setUser({ email : action.payload.email})
    window.location.href = '/'
  } catch (ex) {
    yield put(userActions.fail(ex))
    yield put(githubActions.openAlert({message : 'Usuário não encontrado!'}))
  }
}

export default fetchUserRepos;
