import React,{Component} from 'react'
import {getUser,getReposList, GET_USER_REPOS} from "../../redux/actions";
import {connect} from "react-redux";

class SearchUser extends Component{
    constructor(props) {
        super(props)
        this.state = { user : ''}
    }
    onGetRepos = () =>{
        console.log('repos digitado',this.user.current.value)
        this.props.onGetRepos(this.user.current.value)
    }
    setUser = (e) => {
        this.setState({user : e.target.value})
    }
    render() {
        const { repos } = this.props
        return (
            <React.Fragment>
                Digite o usuário <input type='text' value={this.state.user} onChange={this.setUser}/>
                <button onClick={this.onGetRepos}>Pesquisar</button>
                <p>Total {repos && repos.length} </p>
                <table>
                    <thead>
                    <tr>
                        <th>Repositórios</th>
                        <th></th>
                    </tr>
                    </thead>
                    <tbody>
                    {repos && repos.map((item)=>(
                        <tr>
                            <td>{item.name}</td>
                            <td><a href={`/${this.user.curremt.value}/${item.name}/commits`}>Ver commits</a></td>
                        </tr>
                    ))}
                    </tbody>
                </table>
            </React.Fragment>

        )
    }
}

const mapStateToProps = state => {
    return {
        repos: getReposList(state),
        user : getUser(state),
    }
}
const mapDispatchToProps = dispatch => ({
    onGetRepos (user) {
        dispatch({
            type: GET_USER_REPOS,
            payload: {user}
        })
    }
})

export default connect((state) =>
    mapStateToProps,
    mapDispatchToProps
)(SearchUser)
