import React , { useEffect } from 'react';
import SearchUser from './seachUser'
import {connect} from "react-redux";
import {GET_USER_REPOS, getReposList, getUser} from "../../redux/actions";
import { startReposSaga } from '../../redux'

const mapStateToProps = state => {
    return {
        repos: getReposList(state),
        user : getUser(state),
    }
}

const mapDispatchToProps = dispatch => ({
    onGetRepos (user) {
        dispatch({
            type: GET_USER_REPOS,
            payload: {user}
        })
    }
})

const SearchUserConnected = () => {
    useEffect(()=>{
        startReposSaga()
    })
    return (
        connect((state) =>
             mapStateToProps,
            mapDispatchToProps
        )(SearchUser)
    )
}

export default SearchUserConnected
