import React, {useEffect} from "react";
import ListCommits from './listCommits'
import {getCommits, SELECTED_REPO} from "../../redux/actions";
import {connect} from "react-redux";
import {startReposSaga} from "../../redux";


const mapStateToProps = state => {
    return {
        commits: getCommits(state),
    }
}
const mapDispatchToProps = dispatch => ({
    onSelectedRepo (user,repo) {
        dispatch({
            type: SELECTED_REPO,
            payload: {user,repo}
        })
    }
})
const ListCommitsConnected =  connect((state) =>
    mapStateToProps,
    mapDispatchToProps
)(ListCommits)

export default () => {
    useEffect(()=>{
        startReposSaga()
    })
    return (
        <ListCommitsConnected />
    )
}
