import { call, put, take,takeEvery } from 'redux-saga/effects'
import  { getUserRepos } from '../../../apis/github.api'
import {GET_USER_REPOS, githubActions} from '../../actions'

function* fetchUserRepos(action) {
  try {
    console.log('action',action)
    const response  = yield call(getUserRepos,action.payload.user)
    yield put(githubActions.store({ user : action.payload.user, repos : response.data,}))
  } catch (ex) {
    yield put(githubActions.fail(ex))
  }
}

function* reposSaga() {
  yield takeEvery([GET_USER_REPOS, fetchUserRepos])
}

export default fetchUserRepos;
