import React from 'react';
import logo from './logo.svg';
import { connect } from 'react-redux'
import { githubActions } from './redux/actions/github-actions'
import { startServer  } from './infra/api-config'
import { startSagaRoot } from './redux/sagas'
import './App.css';

class App extends React.Component{
    constructor(props){
        super(props)
        this.state = {
            user : ''
        }
    }
    componentDidMount() {
        startServer()
        startSagaRoot()
        console.log(`aqui`)
        //this.user = getUser()
    }
    onGetRepos = () =>{
        this.props.onGetRepos(this.state.user)
    }

    render(){
        const { user } = this.props;
        return (
            <React.Fragment>
                Digite o usuário <input type='text' value={user} />
                <button onClick={this.onGetRepos}>Pesquisar</button>
                <table>
                    <thead>
                        <tr>
                            <th>Repositórios</th>
                            <th></th>
                        </tr>
                    </thead>
                    <tbody>
                        <tr>
                            <td>teste</td>
                            <td><a href='#'>Ver commits</a></td>
                        </tr>
                    </tbody>
                </table>
            </React.Fragment>
       );
    }
}
const mapStateToProps = state => {
    return {
        repos: ''
    }
}
const mapDispatchToProps = dispatch => {
    return {
        onGetRepos: user => {
            dispatch(githubActions(user))
        }
    }
}


export default connect(
    mapStateToProps,
    mapDispatchToProps
)(App)
