import React, { Component } from 'react'
import styled from 'styled-components'
import './input.css'

const InputStyled = styled(Input)`
        font-size: 1rem;
        box-sizing: border-box;
        display: block;
        width: 100%;
        padding: 0.8rem;
        margin: 0 0 1rem;
        border: 1px solid black;
        border-radius: 5px;
    }
    :focus {
        outline: none;
        border-color: #a02a2a;
        box-shadow: 0 0 10px #a02a2a;
    }
    .input__erro {
        color: red;
    }

`
class Input extends Component {
    constructor(props){
        super(props)
        this.state={ 
            message : null
        }
        this.value = ''
    }
    getValue = () => {
        return this.value
    }
    hasError = () => {
        if(this.state.message === null || this.state.message !== ''){
            return true
        }else{
            return false
        }
    }
    handleChange = (e) => {
        this.value = e.target.value
        const regex = /^(([^<>()[\]\\.,;:\s@"]+(\.[^<>()[\]\\.,;:\s@"]+)*)|(".+"))@(([[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/
        let message = ''
        if(this.props.required && this.value.trim() === ''){
            message = 'Campo Obrigat칩rio'   
        }else if(this.value && this.props.minLength && this.value.length < (this.props.minLength)){
            message = `Digite pelo menos ${this.props.minLength} caracteres`
        }else if(this.props.type==='email' && !regex.test(this.value)){
            message= 'Digite um email v치lido'
        }
       this.setState({ message : message  },this.props.onChange)
    }
    render() {
        return (
            <React.Fragment>
                 <input 
                    onChange={this.handleChange}
                    onBlur={this.handleChange}
                    type={this.props.type}
                    id={this.props.id}
                    className='input'
                    placeholder={this.props.placeholder}
                    required={this.props.required}
                 /> 
                <p className='input__erro'>{this.state.message}</p> 
            </React.Fragment>
        )
    }
}

export default InputStyled
