import React,{Component} from 'react'
import {getUser,getReposList, GET_USER_REPOS} from "../../redux/actions";
import {connect} from "react-redux";

class SearchUser extends Component{
    constructor(props) {
        super(props)
        this.user = React.createRef()
    }
    onGetRepos = () =>{
        console.log('user digitado',this.user.current.value)
        this.props.onGetRepos(this.user.current.value)
    }
    render() {
        const { repos } = this.props
        return (
            <React.Fragment>
                Digite o usuário <input type='text' ref={this.user}/>
                <button onClick={this.onGetRepos}>Pesquisar</button>
                <p>Total {this.repos.leadingComments} </p>
                <table>
                    <thead>
                    <tr>
                        <th>Repositórios</th>
                        <th></th>
                    </tr>
                    </thead>
                    <tbody>
                    {this.props.repos && this.props.repos.map((item)=>(
                        <tr>
                            <td>{item.name}</td>
                            <td><a href='#'>Ver commits</a></td>
                        </tr>
                    ))}
                    </tbody>
                </table>
            </React.Fragment>

        )
    }
}

const mapStateToProps = state => {
    return {
        repos: getReposList(state),
        user : getUser(state),
    }
}
const mapDispatchToProps = dispatch => ({
    onGetRepos (user) {
        dispatch({
            type: GET_USER_REPOS,
            payload: {user}
        })
    }
})

export default connect((state) =>
    mapStateToProps,
    mapDispatchToProps
)(SearchUser)
