import { START_LOADING,STOP_LOADING } from '../../actions/loading'

const loadingReducer = (
    state = {},
    action
) => {
    switch (action.type) {
        case START_LOADING:
            return Object.assign({}, state, {
                isLoading : {...action.payload}
            })
        case STOP_LOADING:
            return Object.assign({}, state, {
                isLoading : {...action.payload}
            })
        default:
            return state
    }
}

export default loadingReducer
