import { takeEvery, all } from 'redux-saga/effects'
import { GET_USER_REPOS } from '../actions'
import fetchUserRepos from './user'


function* rootSaga() {
  yield all([
          takeEvery(GET_USER_REPOS, fetchUserRepos),
          //takeEvery(CLOSE_ALERT,closeAlert)
        ])
}


export default rootSaga;
