import React from 'react';
import { connect } from 'react-redux'
import { githubActions, getUser } from './redux/actions/github-actions'
import { startServer  } from './infra/api-config'
import { startSagaRoot } from './redux'
import { Provider } from 'react-redux'
import SearchUser from './pages/seachUser'
import ListCommits from './pages/listCommits'
import {applyMiddleware, createStore} from "redux";
import reducer from "./redux/reducers";
import store from './redux'
import { Switch, Route } from 'react-router'
import {startCommitsSaga} from "../../redux/sagas";

class App extends React.Component{
    componentDidMount() {
        startCommitsSaga()
    }

    render(){
        return (
            <React.Fragment>
                <Provider store={store}>
                    <Switch>
                            <Route path='/:user/:repo/commits' component={ListCommits} />
                            <Route path='/' component={SearchUser} />
                    </Switch>
                </Provider>
            </React.Fragment>
       );
    }
}

export default App
