import React from 'react'
import {connect} from "react-redux";
import {GET_USER_REPOS, getReposList, getRepoSelected, SELECTED_REPO} from "../../redux/actions";
import {startSagaRoot} from "../../redux";

class ListCommits extends React.Component {
    constructor(props){
        super(props);
        this.state = {
            filter : ''
        }
    }
    componentDidMount() {
        startSagaRoot()
        console.log('props',this.props.match.params)
        const { user ,repo } = this.props.match.params
        this.props.onSelectedRepo(this.props.match.params.repo)
    }

    onFilterCommits = (e) =>{
        this.setState({ filter : e.target.value})
    }
    render(){
        const { commits,repo } = this.props
        return (
            <React.Fragment>
                Reposit√≥rio: {this.props.match.params.repo} <br></br>
                Digite o nome do commit <input type='text' onChange={this.onFilterCommits} value={this.state.filter} />
                <table>
                    <thead>
                    <tr>
                        <th>Commits</th>
                    </tr>
                    </thead>
                    <tbody>
                    {commits && commits.filter((item)=>(
                       item.name === this.state.filter && <tr>
                            <td>{item.name}</td>
                        </tr>
                    ))}
                    </tbody>
                </table>
            </React.Fragment>
        )
    }
}

const mapStateToProps = state => {
    return {
        commits: getReposList(state),
    }
}
const mapDispatchToProps = dispatch => ({
    onSelectedRepo (repo,user) {
        dispatch({
            type: SELECTED_REPO,
            payload: {repo,user}
        })
    }
})

export default connect((state) =>
        mapStateToProps,
        mapDispatchToProps
)(ListCommits)
