import React from 'react'
import {connect} from "react-redux";
import {getCommits,SELECTED_REPO} from "../../redux/actions";
import {startSagaRoot} from "../../redux";

class ListCommits extends React.Component {
    constructor(props){
        super(props);
        this.state = {
            filterCommits : ''
        }
    }
    componentDidMount() {
        startSagaRoot()
        const { user ,repo } = this.props.match.params
        this.props.onSelectedRepo(user,repo)
    }

    onFilterCommits = (e) =>{
        const value = e.target.value
        const commitsFilters  = this.props.commits.filter((item)=>(
              item.commit.message.toLowerCase().indexOf(value) !== -1
         ))
        this.setState({ filter : commitsFilters})
    }
    render(){
        const commits = this.state.filterCommits.length > 0
            ?  this.state.filterCommits
            : this.props.commits
        return (
            <React.Fragment>
                Reposit√≥rio: {this.props.match.params.repo} <br></br>
                Digite o nome do commit <input type='text' onChange={this.onFilterCommits} value={this.state.filter} />
                <table>
                    <thead>
                    <tr>
                        <th>Author</th>
                        <th>Date</th>
                        <th>Mensagem</th>
                    </tr>
                    </thead>
                    <tbody>
                    {commits && commits.map((item) => (
                                <tr>
                                    <td>{item.commit.author.name}</td>
                                    <td>{item.commit.author.date}</td>
                                    <td>{item.commit.message}</td>
                                </tr>
                            )
                    )}
                    </tbody>
                </table>
            </React.Fragment>
        )
    }
}

const mapStateToProps = state => {
    return {
        commits: getCommits(state),
    }
}
const mapDispatchToProps = dispatch => ({
    onSelectedRepo (user,repo) {
        dispatch({
            type: SELECTED_REPO,
            payload: {user,repo}
        })
    }
})

export default connect((state) =>
        mapStateToProps,
        mapDispatchToProps
)(ListCommits)
