import SearchUser from './seachUser'
import {connect} from "react-redux";
import {GET_USER_REPOS, getReposList, getUser} from "../../redux/actions";

import {startCommitsSaga} from '../../redux/sagas/'

startCommitsSaga()

const mapStateToProps = state => {
    return {
        repos: getReposList(state),
        user : getUser(state),
    }
}
const mapDispatchToProps = dispatch => ({
    onGetRepos (user) {
        dispatch({
            type: GET_USER_REPOS,
            payload: {user}
        })
    }
})

export default connect((state) =>
        mapStateToProps,
    mapDispatchToProps
)(SearchUser)
